css:
.print-format {
    font-family: "Times New Roman", Times, serif;
    color:#000000!important;
}
.table.contract-no-table tr td {
    padding: 0!important;
}
h6.contract-no {
    padding: 10px 0 0;
}
h6 {
    background-color: #f1f1f1;
    margin: 0;
    padding: 10px 0;
    font-weight: bold;
    text-align: center;
}
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {

    border-top: none!important;
}
table.table {
    margin: 0;
}
.table.signature-table .table tr td {
    padding: 0!important;
}
table.table tr td:last-child {
    direction: rtl;
}
table.table.signature-table tr td:last-child {
    direction: ltr;
}
table.table.signature-table tr td:last-child .table tr td {
    direction: rtl;
}
@media print {
    table td div {
        page-break-inside: auto !important;
    }

    table {
        page-break-after: auto !important;
    }

    tr {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    td {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    th {
        page-break-inside: avoid;
        page-break-after: auto;
    }

    #thead {
        display: table-header-group;

    }

    tfoot {
        display: table-footer-group;
    }

}
