css:
/* Reset some default styles */
body, h1, h2, h3, p, ul, li, table, th, td {
    margin: 0;
    padding: 0;
}

body {
    font-family: Arial, sans-serif;
    margin: 20px;
}

.letter {
    width: 100%;
    margin: 0 auto;
}

.date {
    font-size: 14px;
}

.salutation {
    font-size: 14px;
    margin-top: 10px;
}

.header {
    font-size: 18px;
    font-weight: bold;
    margin-top: 20px;
}

.salary-table {
    width: 90%;
    border-collapse: collapse;
    margin-top: 15px;
    margin-left: 25px !important;
    margin-bottom: 12px;
}

.salary-table th, .salary-table td {
    border: 1px solid #000;
    padding: 2px !important;
    text-align: center;
    font-size: 15px;
}

.salary-table th {
    background-color: #12A8CE;
    color: #fff;
}

ul {
    margin-top: 10px;
    list-style: disc;
    padding-left: 45px;
}

.signature-block {
    margin-top: 100px;
    width: 100%;
}
.lableblock {
  width: 100%;
  margin-top: 25px;
}

.leftblock {
  font-size: 14px;
  float: left;
  width: 50%;
}

.rightblock {
  float: left;
  width: 50%;
  font-weight: bold;
}
/* Add your own styles as needed */
